mapper.update:
UPDATE dish
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status},
WHERE id = #{id}

UPDATE dish
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT d.*, c.name category_name
FROM dish d LEFT JOIN category c ON d.category_id = c.id


        AND d.category_id = #{categoryId}


        AND d.name LIKE CONCAT('%', #{name}, '%')


        AND d.status = #{status}


ORDER BY d.update_time DESC

SELECT d.*, c.name category_name
FROM dish d LEFT JOIN category c ON d.category_id = c.id
WHERE d.id = #{id}
