mapper.update:
UPDATE orders
SET status = 6, cancel_time = #{cancelTime}
WHERE id = #{id}

mapper.select:
SELECT * FROM orders

    AND status = #{status}
    AND user_id = #{userId}
    AND number = #{number}
    AND phone = #{phone}
    AND order_time >= #{beginTime}
    AND order_time <= #{endTime}

ORDER BY order_time DESC
