mapper.update:
UPDATE shopping_cart SET number = number + 1

    AND user_id = #{userId}
    AND dish_id = #{dishId}
    AND setmeal_id =#{setmealId}
    AND dish_flavor = #{dishFlavor}

UPDATE shopping_cart SET number = number - 1

    AND user_id = #{userId}
    AND dish_id = #{dishId}
    AND setmeal_id =#{setmealId}
    AND dish_flavor = #{dishFlavor}

mapper.delete:
DELETE FROM shopping_cart

    AND user_id = #{userId}
    AND dish_id = #{dishId}
    AND setmeal_id =#{setmealId}
    AND dish_flavor = #{dishFlavor}

mapper.select:
SELECT number FROM shopping_cart

    AND user_id = #{userId}
    AND dish_id = #{dishId}
    AND setmeal_id =#{setmealId}
    AND dish_flavor = #{dishFlavor}
